#include "ulongnum.h"

/*--------------------------------------------------------
static definition - only once at the start
Change to false, if you don't need verbose
----------------------------------------------------------*/
template <typename T>
bool darray<T>::_display = false;

bool str::_display = false;
bool ulongnum::_display = false;

/*--------------------------------------------------------
local to this file. Change verbose = true for debugging
----------------------------------------------------------*/
static bool verbose = false;

/*----------------------------------------------------------------
test multiplication
-----------------------------------------------------------------*/
static void test_multiplication() {
  ulongnum a(789);
  cout << "a = " << a << endl;
  ulongnum b("56");
  cout << "b = " << b << endl;
  ulongnum ans = a * b;
  cout << "ans = " << ans << endl;
  assert(ans == 44184);

  ulongnum rsa129("114381625757888867669235779976146612010218296721242362562561842935706935245733897830597123563958705058989075147599290026879543541");
  ulongnum p1("3490529510847650949147849619903898133417764638493387843990820577");
  ulongnum p2("32769132993266709549961988190834461413177642967992942539798288533");
  ulongnum p1p2 = p1 * p2;
  cout << "p1 = " << p1 << endl;
  cout << "p2 = " << p2 << endl;
  cout << "p1p2 = " << p1p2 << endl;
  assert(p1p2 == rsa129);
}

/*----------------------------------------------------------------
test addition
-----------------------------------------------------------------*/
static void test_addition() {
  ulongnum a(9789);
  ulongnum b("100000");
  ulongnum c('7');
  cout << "a = " << a << endl;
  cout << "b = " << b << endl;
  cout << "c = " << c << endl;
  ulongnum sum = a + 78 + b + c;
  cout << "sum = a + 78 + b + c = " << sum << endl;
  assert(sum == 109874);
}

/*----------------------------------------------------------------
test basic
-----------------------------------------------------------------*/
static void test_basic() {
  ulongnum a(789);
  cout << "a = " << a << endl;
  ulongnum b("56789");
  cout << "b = " << b << endl;
  ulongnum c("123456789012345678901234567890123456789012345678901234567890");
  cout << "c = " << c << endl;
  ulongnum ta(a);
  cout << "ta = " << ta << endl;
  ta = b;
  cout << "ta = " << ta << endl;
}

/*----------------------------------------------------------------
test factorial
-----------------------------------------------------------------*/
static void test_factorial() {
  // http://puzzles.nigelcoldwell.co.uk/nineteen.htm
  {
    const char* fact100 = "93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000";
    clock_t start = clock();
    ulongnum c100;
    c100.factorial(100);
    cout << "Factorial of 100 = " << endl;
    cout << c100 << endl;
    assert(c100 == fact100);
    clock_t end = clock();
    double d = double(end - start) / CLOCKS_PER_SEC;
    cout << "Run time for !100 = " << " is " << d << " secs" << endl;
  }

  //http://justinwhite.com/big-calc/1000.html
  {
  const char* fact1000 = "";
  clock_t start = clock();
  ulongnum c1000;
  c1000.factorial(1000);
  cout << "Factorial of 1000 = " << endl;
  cout << c1000 << endl;
  assert(c1000 == fact1000);
  clock_t end = clock();
  double d = double(end - start) / CLOCKS_PER_SEC;
  cout << "Run time for !1000 = " << " is " << d << " secs" << endl;
}
}

/*----------------------------------------------------------------
main
-----------------------------------------------------------------*/
int main() {
  ulongnum::set_display(verbose);
  test_basic();
  test_addition();
  test_multiplication();
  test_factorial();
  return 0;
}

